secureScript.script:
import hudson.util.RemotingDiagnostics
import jenkins.model.Jenkins

String agent_name = 'repo'
//groovy script you want executed on an agent
groovy_script = '''
def repo = "aptly repo list --raw".execute() | "grep -v tmaxgooroom-3.0-unstable".execute() | "grep -v tmaxgooroom-3.0-updates".execute() | "grep -e unstable -e experimental -e updates".execute()
println repo.text
'''.trim()

String result
Jenkins.instance.slaves.find { agent ->
    agent.name == agent_name
}.with { agent ->
    result = RemotingDiagnostics.executeGroovy(groovy_script, agent.channel).replaceAll("_main", "")
}
return result.tokenize()

hudson.tasks.Shell.command:
#!/bin/bash
set -e #set -x
DISTRIBUTION=${DISTRIBUTION_NAME} COMPONENT="main" TARGETDIR="${WORKSPACE}" CONFIG="/etc/aptly.conf" BUSINESSNAME=`echo ${DISTRIBUTION} | cut -d '-' -f 2`
function add_repo() { aptly repo add -config=${CONFIG} ${DISTRIBUTION}_${COMPONENT} ${TARGETDIR} }
function remove_old_package() { dup=false
for p in `aptly repo search ${DISTRIBUTION}_${COMPONENT} | sort -V` do pkg=`echo $p | sed 's,_.*,,'` if test "$pkg" = "$pkg_old" then dup=true elif $dup then dup=false v_old=`echo $p_old | cut -d_ -f2` aptly repo remove ${DISTRIBUTION}_${COMPONENT} "$pkg_old (<< $v_old)" fi p_old="$p" pkg_old="$pkg" done
if test "$pkg" = "$pkg_old" then v_old=`echo $p_old | cut -d_ -f2` aptly repo remove ${DISTRIBUTION}_${COMPONENT} "$pkg_old (<< $v_old)" fi }
function package_version_check() { REVISION=`echo ${PACKAGE} | cut -d_ -f2 | rev | cut -d '+' -f 1 | rev | sed 's/[0-9]*\$//g' ` PACKAGE_NAME=`echo ${PACKAGE} | cut -d_ -f1`
if [[ ${PACKAGE_NAME} == *"gooroom-grub"* ]]; then echo -e "this is gooroom-grub package, free pass" else if [[ ${PACKAGE} == *"+"* ]] && [ ${REVISION} != ${BUSINESSNAME} ]; then echo -e "wrong package rivision!" exit 1 fi fi }
mkdir -p ${TARGETDIR}
if [ ! -e "packages.zip" ]; then echo -e "packages.zip not found" exit 1 fi
unzip packages.zip -d ${TARGETDIR}/
TAR_FILE=`find ${TARGETDIR} -name "*.tar.*" | awk 'NR==1{print $1}'` DSC_FILE=`find ${TARGETDIR} -name "*.dsc"` DEB_FILE=`find ${TARGETDIR} -name "*.deb" | awk 'NR==1{print $1}'` SOURCE_NAME=`basename ${DSC_FILE} | sed 's/\.dsc//' | cut -d_ -f1` VERSION_NAME=`basename ${DSC_FILE} | sed 's/\.dsc//' | cut -d_ -f2`
function check_package() { if [ -z ${DEB_FILE} ]; then echo -e "*.deb file not found" exit 1 fi
if [ -z ${TAR_FILE} ]; then echo -e "*.tar.* file not found" exit 1 fi
if [ -z ${DSC_FILE} ]; then echo -e "*.dsc file not found" exit 1 fi
for p in `find ${TARGETDIR} -name "*.deb"` do PACKAGE=`basename $p | sed 's/\.deb//'` package_version_check done }
if [[ ${DISTRIBUTION} == *"experimental"* ]]; then add_repo remove_old_package else check_package add_repo remove_old_package fi
